const start = Math.floor(Math.random() * (dba.length - 10))
const db = dba.slice(start, start + 10)

function DeckManager ({ nUsers }) {
  const [endState, setEndState] = useState(false)
  const votes = useRef(new Array(db.length).fill(0))
  const swipeHistory = useRef(new Array(nUsers).fill(0))

  return endState ? (
    <div>
      Your match is {}
    </div>
  ) : (
    <div className='flexContainer deckManager'>
      {Array.from(Array(nUsers).keys()).map((i) =>
        <Deck id={i} db={db} votes={votes} swipeHistory={swipeHistory} setEndState={setEndState}/>
      )}
    </div>
  )
}

export default DeckManager